package com.expo.base.utils;import android.content.SharedPreferences;import android.preference.PreferenceManager;import com.expo.base.BaseApplication;/** * 状态信息存储帮助类SP */public class PrefsHelper {    private static SharedPreferences getPrefs() {        return PreferenceManager.getDefaultSharedPreferences( BaseApplication.getApplication() );    }    public static boolean getBoolean(String key, boolean defaultValue) {        return getPrefs().getBoolean( key, defaultValue );    }    public static boolean exists(String key) {        return getPrefs().contains( key );    }    public static void setBoolean(String key, boolean value) {        getPrefs().edit().putBoolean( key, value ).apply();    }    public static String getString(String key, String defaultValue) {        return getPrefs().getString( key, defaultValue );    }    public static void setString(String key, String value) {        getPrefs().edit().putString( key, value ).apply();    }    public static void setLong(String key, Long value) {        getPrefs().edit().putLong( key, value ).apply();    }    public static long getLong(String key, long defaultValue) {        return getPrefs().getLong( key, defaultValue );    }    public static void remove(String keyDownloadId) {        getPrefs().edit().remove( keyDownloadId ).apply();    }}